import React , {useState,createContext , useContext} from 'react' ;

import {storeProducts , detailProduct} from '../data'

export const CreateMainContext = createContext(storeProducts)

export const MainContextProvider = (props) => {

    // states
    const [mainState , setMainState] = useState({
        products : storeProducts , 
        detailsProduct : detailProduct ,  
    })
    const [modal , setModal] = useState({
        modalOpen : false , 
        modalProduct : detailProduct ,
    }) 
    const [cart , setCart] = useState([])
    const [cartFunc , setCartFunc] = useState({
        cartSubTotal : 0 ,
        cartTax : 0 , 
        cartTotal : 0
    })


    // common functions 
    const getFilterItem = (id) => {
        return mainState.products.filter(item => item.id === id)
    }
    const getFindItem = (id) => {
        return mainState.products.find(item => item.id === id)
    }
    const getDetail = (id) => {
        const findDetailProduct = getFindItem(id)
        const updateMainState = {
            ...mainState , 
            detailsProduct : findDetailProduct
        }
        setMainState(updateMainState)
    }

    // const updateItemToMainState = (id) => {
    //     const findCartProduct = getFindItem(id)
    //     const allProducts = mainState.products
    //     const index = allProducts.indexOf(findCartProduct)
    //     console.log(index);
    //     const product = allProducts[index] ;
    //     console.log(product);
    // }

    const updateMainStateProductData = (product , inCart , count , total) => {
        product.inCart = true ;
        product.count = product.count + 1
        const price = product.price
        product.total = price 
    }


    // modal funcitions
    const openModal = (id) => {
        const findModalProduct = getFindItem(id)
        const updateModal = {
            ...modal , 
            modalOpen : true  ,
            modalProduct : findModalProduct
        }
        setModal(updateModal)
    }   
    const closeModal = () => {
        const updateModalState = {
            ...modal , 
            modalOpen : false  ,
        }
        setModal(updateModalState)
    }


    // cart functions 
    const addToCart = (id) => {
        const findCartProduct = getFindItem(id)
        const allProducts = mainState.products
        const index = allProducts.indexOf(findCartProduct)
        console.log(index);
        const product = allProducts[index] ;
        // console.log(product);
        updateMainStateProductData(product , true , 1 , product.price) ;
        // product.inCart = true ;
        // product.count = product.count + 1
        // const price = product.price
        // product.total = price 
        // console.log(mainState.products);
        // allProducts.map(item => )

        setCart([...cart , product])
    }

    const removeItem = (id) => {
        console.log('rmv' + id);

        const findCartProduct = getFindItem(id) ;
        const allProducts = mainState.products
        const index = allProducts.indexOf(findCartProduct)
        const product = allProducts[index]
        product.inCart = false 
        product.count = 0 
        product.total = 0 
        const updatedCart = cart.filter(item => item.id !== id)
        setCart(updatedCart)
    }

    const removeItemFromCart = (id) => {
        removeItem(id)
    }


    return(
        <CreateMainContext.Provider
            value={{
                mainState : mainState , 
                setMainState : setMainState , 
                cart : cart , 
                getDetail : getDetail ,
                addToCart : addToCart , 
                removeItemFromCart : removeItemFromCart , 
                modal : modal ,
                openModal : openModal , 
                closeModal : closeModal ,

            }}
        >
            {props.children}
        </CreateMainContext.Provider>
    )
}

export const UseMainContext = () => {
    return useContext(CreateMainContext)
}
