    const product = ctx.mainState.detailsProduct


    return (
        <div className='container'>
            <div className="row">
                <div className="col-10 mx-auro text-center text-slanted text-blue my-5">
                    <h2>{product.title}</h2>
                </div>
            </div>
        </div>
    )
}

export default ProductDetail
