import React from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { UseMainContext } from '../../context/Context';
import {FaCartPlus} from 'react-icons/fa' 

const Product = (props) => {

    const product = props.product
    // console.log(product);

    const ctx = UseMainContext() ;
    // console.log(ctx);


    return (
        <ProductWrapper className='my-3'>
            <div className="card">

                <div
                 onClick={() => ctx.getDetail(product.id)} 
                 className="img__container p-5">
                     <Link to='/detail'>
                         <img src={product.img} className='card__img__top img-fluid' alt="image" />
                     </Link>
                     <button
                        onClick={() => {
                            ctx.openModal(product.id)
                            ctx.addToCart(product.id)
                            }
                        }
                     className="cart__btn">
                         <FaCartPlus/>
                     </button>
                </div>

                <div className="card-footer d-flex justify-content-between">
                    <p className="align-self-center mb-0">
                        {product.title}
                    </p>
                    <h5 className="text-blue font-italic mb-0">
                        <span className="mr-1">$</span>
                        {product.price}
                    </h5>
                </div>

            </div>
        </ProductWrapper>
    )
}

const ProductWrapper = styled.div`
    .card{
        border-color: transparent;
        transition: all 1s linear;

        .img__container{
            postion : relative ;
            overflow : hidden ;

            .card__img__top{
                transition : all 1s ease-in-out ;
            }

            .cart__btn {
                position: absolute;
                bottom: 0;
                right: 0;
                padding: 0.2rem 0.4rem;
                background: var(--lightBlue);
                border: none;
                color: var(--mainWhite);
                font-size: 1.4rem;
                border-radius: 0.5rem 0 0 0;
                transform: translate(100%, 100%);
                transition: all 1s ease-in-out;
              }

            &:hover{
                .card__img__top{
                    transform : scale(1.2) 
                }
                .cart__btn{
                    transform : translate(0,0)
                }
            }

        }


        .card-footer {
            background: transparent;
            border-top: transparent;
            transition: all 1s linear;
        }
        &:hover{
            border: 0.04rem solid rgba(0, 0, 0, 0.2);
            box-shadow: 2px 2px 5px 0px rgba(0, 0, 0, 0.2);
            .card-footer{
                background: rgba(247, 247, 247);
            }
        }
    }
`

export default Product
